package com.agent.czb.core.park.enums;import com.agent.czb.common.enums.BaseEnum;/** * @author linkai * @since 16/9/12 */public interface ParkCarStateEnum {    enum State implements BaseEnum {        进("1", "进"), 出("2", "出"), 已支付("3", "已支付"), 车辆超时("5", "车辆超时"),;        private String value;        private String desc;        State(String value, String desc) {            this.value = value;            this.desc = desc;        }        public static ParkGateEnum.State get(String value) {            ParkGateEnum.State[] ens = ParkGateEnum.State.values();            for (ParkGateEnum.State en : ens) {                if (en.toValue().equals(value)) {                    return en;                }            }            return null;        }        public static String getDesc(String value) {            ParkGateEnum.State state = get(value);            if (state != null) {                return state.toDesc();            }            return null;        }        public String toValue() {            return value;        }        public String toDesc() {            return desc;        }        public String toString() {            return desc;        }    }}